    /**
     * User's phone number.
     * TODO: Implement phone number validation
     * TODO: Consider international phone number support
     */
    @Pattern(
        regexp = "^\\+?[1-9]\\d{1,14}$",
        message = "Phone number must be valid international format"
    )
